left join sys_user u on s.create_by = u.id and u.del_flag=#{delFlag}
left join sys_user h on s.update_by = h.id and h.del_flag=#{delFlag}
where 
	s.del_flag=#{delFlag}
and s.status = '1'
and 
	((DATE_FORMAT(s.start_date, '%Y-%m-%d') >=   DATE_FORMAT(#{startDate}, '%Y-%m-%d'))
	or 
	(DATE_FORMAT(s.start_date, '%Y-%m-%d') <=   DATE_FORMAT(#{startDate}, '%Y-%m-%d')
	 and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=   DATE_FORMAT(#{startDate}, '%Y-%m-%d')
	))
and 
DATE_FORMAT(s.start_date, '%Y-%m-%d') <=   DATE_FORMAT(#{endDate}, '%Y-%m-%d')
and 
	c.id=#{comboId}
and 
	a.id=#{advId}
