select 

from ad_sell s 
left join ad_combo c on s.ad_combo_id = c.id and c.del_flag=#{delFlag}
left join ad_advertiser a on s.advertiser_id = a.id and a.del_flag=#{delFlag}
where 
	s.del_flag=#{delFlag}
and 
	DATE_FORMAT(s.start_date, '%Y-%m-%d') <=   DATE_FORMAT(#{startDate}, '%Y-%m-%d')

and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=   DATE_FORMAT(#{endDate}, '%Y-%m-%d')
and 
	c.id=#{comboId}
and 
	a.id=#{advId}
order by s.start_date

		select  count(DISTINCT c.id) as c_ount from ad_sell s
		left join ad_combo c on s.ad_combo_id =c.id and c.del_flag=#{delFlag}
		left join ad_type t on c.ad_type_id = t.id and t.del_flag=#{delFlag}
		where s.del_flag=#{delFlag}
		and
		 (
		(DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		) or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
		)
		)
 		and t.id=#{typeId}

		select  
		,

		from ad_sell s
		left join ad_combo c on s.ad_combo_id =c.id and c.del_flag=#{delFlag}
		left join ad_type t on c.ad_type_id = t.id and t.del_flag=#{delFlag}
		where s.del_flag=#{delFlag}

		and
		 (
		(DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		) or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
		)
		)



			 			and c.ad_combo_name  like CONCAT('%', #{adCombo.comboName}, '%')


			 		and t.id=#{adCombo.adType.id}



 		ORDER BY c.ad_combo_name

		select  DISTINCT a.advertiser_id as a_id ,a.`name` as a_name,count(DISTINCT c.id) as c_ount from ad_advertiser a
		left join ad_sell s on a.id = s.advertiser_id and s.del_flag=#{delFlag}
		left join ad_combo c on s.ad_combo_id =c.id and c.del_flag=#{delFlag}
		left join ad_type t on c.ad_type_id = t.id and t.del_flag=#{delFlag}
		where s.del_flag=#{delFlag}
		and
		 (
		(DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		) or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
		)
		)


			 		and t.id=#{adCombo.adType.id}



		    and a.id=#{advertiser.id}

 		group by a_id,a_name order by c_ount desc

SELECT
	sum(
		getSellTime (
			#{startDate},
			#{endDate},
			s.start_date,
			s.end_date
		)
	) AS c_ount,
	s.advertiser_id as a_id,
	a.`name` as a_name
FROM
	ad_sell s
LEFT JOIN ad_combo c on s.ad_combo_id = c.id and c.del_flag=#{delFlag}
LEFT JOIN ad_type t on c.ad_type_id = t.ad_type_id and t.del_flag=#{delFlag}
LEFT JOIN ad_advertiser a on s.advertiser_id = a.id and a.del_flag=#{delFlag}
where s.del_flag=#{delFlag}


		and  t.id = #{adCombo.adType.id}



	and s.advertiser_id=#{advertiser.id}

GROUP BY s.advertiser_id

		select  
		 ,

		from ad_advertiser a
		left join ad_sell s on a.id = s.advertiser_id and s.del_flag=#{delFlag}
		left join ad_combo c on s.ad_combo_id =c.id and c.del_flag=#{delFlag}
		left join ad_type t on c.ad_type_id = t.id and t.del_flag=#{delFlag}
		where s.del_flag=#{delFlag}

		and
		 (
		(DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		) or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
		)
		)



			 		and t.id=#{adCombo.adType.id}


			 		and c.ad_combo_name  like CONCAT('%', #{adCombo.comboName}, '%')



		    and a.advertiser_id=#{advertiser.advertiserId}

 		order by c.ad_combo_name


select a, 
sum( 
case when (a !=0 and a != 23 and c.start_hour <= a  and c.end_hour =a )or(a !=0 and a!=23 and c.start_hour <= a and c.end_hour  >=  b) then 1 
 when (a=0  and c.start_hour =a and c.end_hour < 1 )or(a=0  and c.start_hour = a and c.end_hour  > 1) then 1 
 when (a =23  and c.end_hour =a and c.end_hour =a)or( a =23 and c.start_hour <  a and c.end_hour =23) then 1 
else  0 end ) as c_count from ad_combo  c,
(select 0 a,1 b union all select 1,2 union  all select 2,3 union all select 3,4 union  all select 4,5
union all select 5,6 union  all select 6,7 union all select 7,8 union  all select 8,9
union all select 9,10 union  all select 10,11 union all select 11,12 union  all select 12,13
union all select 13,14 union  all select 14,15 union all select 15,16 union  all select 16,17
union all select 17,18 union  all select 18,19 union all select 19,20 union  all select 20,21
union all select 21,22 union  all select 22,23 union all select 23,24) aa 
where c.id in(
SELECT   DISTINCT c1.id FROM ad_combo c1
left join ad_sell s on c1.id = s.ad_combo_id and s.del_flag=#{delFlag}
where c1.del_flag=#{delFlag}
and  (
		(DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		) or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
		)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
			and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
		)
		)


	 			and c1.ad_type_id=#{adCombo.adType.id}


	 and c1.ad_is_flag=#{adCombo.isFlag}


)
group by a

select count(DISTINCT c.id) from ad_sell s
left join ad_combo c on s.ad_combo_id = c.id and c.del_flag =#{delFlag}
left join ad_combo_network cn on c.id = cn.ad_combo_id
left join ad_network n on cn.ad_network_id = n.id and n.del_flag =#{delFlag}
where s.del_flag = #{delFlag}
and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{nowDate}, '%Y-%m-%d')
and n.id = #{networkId}

select count(DISTINCT c.id) from ad_sell s
left join ad_combo c on s.ad_combo_id = c.id
left join ad_combo_channel cc on c.id = cc.ad_combo_id
left join ad_channel ca on cc.ad_channel_id = ca.id and ca.del_flag =#{delFlag}
left join ad_network n on ca.ad_network_id = n.id and n.del_flag =#{delFlag}
where s.del_flag = #{delFlag}
and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{nowDate}, '%Y-%m-%d')
and n.id = #{networkId}

select max(e.end_date) from ad_element e 
left join ad_combo c on e.ad_combo_id = c.id  and c.del_flag=#{delFlag}
left join ad_sell s on c.id = s.ad_combo_id and s.del_flag=#{delFlag}
where e.del_flag=#{delFlag}
and e.start_date >=  s.start_date
and e.end_date <=  s.end_date
and s.id=#{sellId}

select count(DISTINCT s.id) from ad_sell s
left join ad_combo c on s.ad_combo_id = c.id and c.del_flag=#{delFlag}
where  
s.del_flag=#{delFlag}
and  s.status = #{status}

and  (
				(DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
					and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
				) or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
					and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
				)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
					and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
					and DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
				)or (DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
					and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
					and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
				)
	 )
and c.id = #{comboId}

	and s.id != #{sellId}

SELECT a1.*,IFNULL(a2.count1,0) publishCount,IFNULL(a3.count2,0) unPublishCount,(IFNULL(a2.count1,0)+IFNULL(a3.count2,0)) allCount,ac.ad_type_id from (SELECT a.ad_combo_id,a.advertiser_id,a.start_date,a.end_date from ad_sell a GROUP BY a.ad_combo_id) a1 LEFT JOIN ad_combo ac on a1.ad_combo_id = ac.id LEFT JOIN (SELECT ad.ad_combo_id ad_combo_id1,count(1) count1 from (SELECT ae.* from ad_element ae where ae.ad_status in (3,4) and ae.del_flag=0) ad GROUP BY ad.ad_combo_id) a2 on a1.ad_combo_id = a2.ad_combo_id1 LEFT JOIN (SELECT ad2.ad_combo_id ad_combo_id2,count(1) count2 from (SELECT ae2.* from ad_element ae2 where ae2.ad_status in (-1,0,1,2) and ae2.del_flag=0) ad2 GROUP BY ad2.ad_combo_id) a3 on a1.ad_combo_id = a3.ad_combo_id2 where ac.del_flag=#{delFlag}
and DATE_FORMAT(a1.start_date, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')
and DATE_FORMAT(a1.start_date, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
and ac.ad_type_id=#{typeId}
and IFNULL(a2.count1,0) ${comboRelease} 0
ORDER BY ac.ad_type_id
limit #{startNum},#{pageSize}

SELECT count(DISTINCT a1.ad_combo_id) as s_combo_count from (SELECT a.ad_combo_id,a.advertiser_id,a.start_date,a.end_date from ad_sell a GROUP BY a.ad_combo_id) a1 LEFT JOIN ad_combo ac on a1.ad_combo_id = ac.id LEFT JOIN (SELECT ad.ad_combo_id ad_combo_id1,count(1) count1 from (SELECT ae.* from ad_element ae where ae.ad_status in (3,4) and ae.del_flag=0) ad GROUP BY ad.ad_combo_id) a2 on a1.ad_combo_id = a2.ad_combo_id1 LEFT JOIN (SELECT ad2.ad_combo_id ad_combo_id2,count(1) count2 from (SELECT ae2.* from ad_element ae2 where ae2.ad_status in (-1,0,1,2) and ae2.del_flag=0) ad2 GROUP BY ad2.ad_combo_id) a3 on a1.ad_combo_id = a3.ad_combo_id2 where ac.del_flag=#{delFlag}
and DATE_FORMAT(a1.start_date, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')
and DATE_FORMAT(a1.start_date, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
and ac.ad_type_id=#{typeId}
and IFNULL(a2.count1,0) ${comboRelease} 0

SELECT b.*,IFNULL(a3.count,0) sellCount from (SELECT @cdate := date_add(@cdate,interval +1 day) sell_date from (SELECT @cdate := date_add(DATE_FORMAT(#{startDate}, '%Y-%m-%d'),interval -1 day) from ad_sell limit #{days}) t1 ) b
LEFT JOIN
(SELECT a2.create_date,count(1) count from (SELECT a1.* from (SELECT a.ad_combo_id,advertiser_id, DATE_FORMAT(a.create_date,'%Y-%m-%d') create_date,ac.ad_type_id from ad_sell a LEFT JOIN ad_combo ac on a.ad_combo_id = ac.id) a1 WHERE a1.ad_type_id = #{typeId}
and a1.advertiser_id=#{advertiserid}
and DATE_FORMAT(a1.create_date, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')
and DATE_FORMAT(a1.create_date, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
) a2 GROUP BY a2.create_date) a3 on b.sell_date = a3.create_date ORDER BY b.sell_date

        select
        ,

        ,
        s.id as s_id,
        s.start_date as s_start_date,
        s.end_date as s_end_date,
        s.del_flag as s_del_flag,
        a.id as a_id,
        a.`name` as a_name
		from ad_advertiser a
		left join ad_sell s on a.id = s.advertiser_id and s.del_flag='0'
		left join ad_combo c on s.ad_combo_id =c.id and c.del_flag='0'

				and c.start_hour <= #{adCombo.startHour}
				and c.end_hour >= #{adCombo.endHour}


			and c.end_hour = #{adCombo.startHour}

		left join ad_type t on c.ad_type_id = t.id and t.del_flag='0'
		where s.del_flag='0'

            and t.id=#{adCombo.adType.id}


            and a.id=#{advertiser.advertiserId}


            and  (
            (DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
            and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
            ) or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
            and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
            )or (DATE_FORMAT(s.start_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
            and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
            and DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
            )or (DATE_FORMAT(s.start_date, '%Y-%m-%d') <=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
            and DATE_FORMAT(s.end_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
            and DATE_FORMAT(s.end_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')
            )
            )

 		order by s.start_date,a.`name` desc

SELECT t.ad_type_id as t_id, t.ad_type_name as t_type_name, IFNULL(tmp1.valid_time,0) as valid_time, IFNULL(tmp2.sell_time,0) as sell_time, IFNULL((sell_time/tmp1.valid_time),0) as percent from (
SELECT c.ad_type_id,round(sum((DATEDIFF( c.valid_end_time, c.valid_start_time )+1)*IFNULL(((end_hour - start_hour)*3600+(end_minutes - start_minutes)*60+(end_second - start_second)),86400)/3600)) as valid_time from ad_combo c where c.del_flag = '0'
and c.valid_start_time > #{startDate}
and c.valid_end_time < #{endDate}
GROUP BY c.ad_type_id
) tmp1 LEFT JOIN (
SELECT c.ad_type_id,round(sum((DATEDIFF(s.end_date,s.start_date)+1)*IFNULL(((c.end_hour - c.start_hour)*3600+(c.end_minutes - c.start_minutes)*60+(c.end_second - c.start_second)),86400)/3600)) as sell_time from ad_sell s LEFT JOIN ad_combo c on s.ad_combo_id = c.id where s.del_flag = '0' and c.del_flag = '0'
and c.valid_start_time > #{startDate}
and c.valid_end_time < #{endDate}
group by c.ad_type_id
) tmp2 on tmp1.ad_type_id = tmp2.ad_type_id LEFT JOIN ad_type t on tmp1.ad_type_id = t.id ORDER BY tmp1.valid_time desc
